package com.example.firebaseauthimport android.content.Contextimport android.preference.PreferenceManagerobject SavedPreference {    private const val EMAIL_KEY = "email_key"    private const val USERNAME_KEY = "username_key"    fun setEmail(context: Context, email: String) {        val sharedPreferences = PreferenceManager.getDefaultSharedPreferences(context)        val editor = sharedPreferences.edit()        editor.putString(EMAIL_KEY, email)        editor.apply()    }    fun getEmail(context: Context): String? {        val sharedPreferences = PreferenceManager.getDefaultSharedPreferences(context)        return sharedPreferences.getString(EMAIL_KEY, null)    }    fun setUsername(context: Context, username: String) {        val sharedPreferences = PreferenceManager.getDefaultSharedPreferences(context)        val editor = sharedPreferences.edit()        editor.putString(USERNAME_KEY, username)        editor.apply()    }    fun getUsername(context: Context): String? {        val sharedPreferences = PreferenceManager.getDefaultSharedPreferences(context)        return sharedPreferences.getString(USERNAME_KEY, null)    }}